import { TQuestionGroup } from '../static/questions';
import { formatToLargeNumber } from '../utils/utils';
import { IResultState, IQuestionState } from '../views/survey/Survey';

// Constrain unrealistic inputs
const constrainLargeNumber: (entry: number) => number = (entry) => {
  return entry > 100000000000 ? 100000000000 : entry;
};

export const calculateResults: (
  selectedGroups: TQuestionGroup[],
  questionsList: IQuestionState[]
) => IResultState[] = (selectedGroups, questionsList) => {
  // Mantaining inside scope as it requires an internal prop

  const getValueFromId: (id: number) => number = (id) => {
    const selectedQuestion = questionsList.find(
      (el) => el.id === id
    ) as IQuestionState;
    return selectedQuestion.type === 'currency' ||
      selectedQuestion.type === 'plain'
      ? parseInt(selectedQuestion.value.toString().split(',').join(''))
      : (selectedQuestion.value as number);
  };

  const finalResults = [] as IResultState[];
  const groupResults = {
    costs: [] as IQuestionState[],
    revenue: [] as IQuestionState[],
    productivity: [] as IQuestionState[],
  };
  selectedGroups.forEach((group) => {
    if (group.checked) {
      questionsList.forEach((question) => {
        if (question.group === group.group) {
          groupResults[group.group].push(question);
        }
      });
    }
  });
  if (groupResults.costs.length) {
    const first = getValueFromId(0) * getValueFromId(2);
    const second = (getValueFromId(1) - 0.5) * getValueFromId(2);
    finalResults.push({
      group: 'costs',
      first: `$${formatToLargeNumber(constrainLargeNumber(first))}`,
      second: `${formatToLargeNumber(constrainLargeNumber(second))} hours`,
    });
  }
  if (groupResults.revenue.length) {
    const first =
      getValueFromId(10) *
      2 *
      (getValueFromId(11) / 100) *
      (getValueFromId(12) / 100) *
      getValueFromId(14);
    const second = first * (getValueFromId(13) / 100);
    finalResults.push({
      group: 'revenue',
      first: `$${formatToLargeNumber(constrainLargeNumber(first))}`,
      second: `$${formatToLargeNumber(constrainLargeNumber(second))}`,
    });
  }
  if (groupResults.productivity.length) {
    const first = getValueFromId(20) * getValueFromId(21) * 0.1;
    const second =
      (getValueFromId(22) / 2000) * getValueFromId(23) * getValueFromId(26) * 2;
    const third =
      (getValueFromId(24) / 2000) * getValueFromId(25) * getValueFromId(26) * 2;
    finalResults.push({
      group: 'productivity',
      first: `${formatToLargeNumber(
        constrainLargeNumber(first + second + third)
      )}`,
    });
  }

  return finalResults;
};
